var MidiWriter = require('.');

var track = new MidiWriter.Track();

track.addEvent([
	new MidiWriter.NoteEvent({pitch: ['E4'], duration: '4', grace:['Eb4']}),
	new MidiWriter.NoteEvent({pitch: ['F#4'], duration: '4', grace:['F4']}),
	new MidiWriter.NoteEvent({pitch: ['G#4'], duration: '4', grace:['G4']}),
	new MidiWriter.NoteEvent({pitch: ['A4'], duration: '4', grace:['G#4']}),
	new MidiWriter.NoteEvent({pitch: ['B4'], duration: '4', grace:['A#4']}),
	new MidiWriter.NoteEvent({pitch: ['C#5'], duration: '4', grace:['C5']}),
	new MidiWriter.NoteEvent({pitch: ['D#5'], duration: '4', grace:['D5']}),
	new MidiWriter.NoteEvent({pitch: ['E5'], duration: '4', grace:['D#5']})
]);

var write = new MidiWriter.Writer([track]);

write.stdout();